import Vue from 'vue'
import VueRouter from 'vue-router'
import Index from '../views/Index.vue'
import Pred from '../views/Pred.vue'
import TableData from '../views/TableData.vue'
import Login from '../views/Login.vue'

Vue.use(VueRouter)

const routes = [{
    path: '/',
    name: 'Index',
    component: Index,
    meta: { 
        name: '首页',
        showHeader: true,
        showBackground: true,
    }
}, {
    path: '/pred',
    name: 'Pred',
    component: Pred,
    meta: { 
        name: "在线预测" ,
        showHeader: true,
        showBackground: true,
    }
}, 
// {
//     path: '/history',
//     name: 'history',
//     component: History,
//     meta: { name: "电网历史" }
// }, 
{
    path: '/tableData',
    name: 'TableData',
    component: TableData,
    meta: { 
        name: "数据表格" , 
        showHeader: true,
        showBackground: true,
    }
},
{
    path: '/login',
    name: 'Login',
    component: Login,
    meta: { 
        name: "登录" , 
        showHeader: false,
        showBackground: false,
    }
}]

const router = new VueRouter({
    mode: 'history',
    base: process.env.BASE_URL,
    routes
})

// 导航守卫
// 使用 router.beforeEach 注册一个全局前置守卫，判断用户是否登陆
router.beforeEach((to, from, next) => {
	// 设置标题
	if (to.meta.title) {
		document.title = to.meta.title
	}

	if (to.path === '/login' || to.path === '/register') {
		next();
	} else {
		let token = sessionStorage.getItem('Authorization');
		if (token === null || token === '') {
			next('/login');
		} else {
			next();
		}
	}
});
export default router
